const all_image=document.querySelectorAll(".carousel_image img"),container=document.querySelector(".carousel_image"),change_btn=document.querySelector(".change__mage_carousel");window.onload=()=>{let e=0,l=all_image.length,n=all_image[0].clientWidth,o=n*e;window.onresize=()=>{n=all_image[0].clientWidth,o=n*e},change_btn.onclick=()=>{o==n*(l-1)?(container.scrollTo(0,0),e=0):(e++,container.scrollTo(n*e,0)),o=n*e}};