const btn_open_nav=document.querySelector(".header_burger"),btn_close_nav=document.querySelector(".closeNavigation"),nav=document.querySelector("#navigation"),element_to_anime=document.querySelectorAll(".openAnimate"),body=document.querySelector("body");let active_nav=!1;document.addEventListener("DOMContentLoaded",()=>{btn_open_nav.onclick=()=>{0==active_nav&&(active_nav=!0,nav.classList.add("open"),body.style.overflow="hidden",nav_apparition(element_to_anime,()=>{active_nav=!1}))},btn_close_nav.onclick=()=>{0==active_nav&&(active_nav=!0,nav_apparition(element_to_anime,()=>{active_nav=!1,nav.classList.remove("open"),body.style.overflow="auto"}))}});const nav_apparition=(e,n)=>{setTimeout(()=>{baseTime=0,e.forEach(e=>{setTimeout(()=>{e.classList.toggle("open")},baseTime),baseTime+=80}),setTimeout(()=>{n()},baseTime)},350)};